import EyeClosed from "../figure/eye-closed.png";
import EyeMiddle from "../figure/eye-middle.png";
import EyeOpen from "../figure/eye-open.png";
import React, { useState, useEffect } from "react";
import { render } from "@testing-library/react";

function DrawEye(){

  const getWindowDimensions = () => {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height,
    };
  };

  let state = {
      eye_mode: (new Date().getSeconds()%3)
  }

  const [eye_mode] = useState(0);

  const getImage = (i) => {
    let image;
    switch (i) {
      case 0:
        image = EyeOpen;
        break;
      case 1:
        image = EyeMiddle;
        break;
      case 2:
        image = EyeClosed;
        break;
    }
    return image;
  };

  const [windowDimensions, setWindowDimensions] = useState(
    getWindowDimensions(),
  );

  useEffect(() => {
    const onResize = () => {
      setWindowDimensions(getWindowDimensions());
    };
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);


    return(
        <img src={getImage(new Date().getSeconds()%3)} width={windowDimensions.width / 2} />
    );
}
export default DrawEye;
