import EyeClosed from "../figure/eye-closed.png";
import EyeMiddle from "../figure/eye-middle.png";
import EyeOpen from "../figure/eye-open.png";
import React, { useState, useEffect } from "react";
import { render } from "@testing-library/react";

function DrawEye(props){

  const getWindowDimensions = () => {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height,
    };
  };

  const [eye_mode,set_eye_mode] = useState(0);

  const getImage = (i) => {
    let image;
    if(toBoolean(props.reverse)){
      console.log(props.reverse);
    }
    switch (i) {
      case 1:
        image = EyeMiddle;
        break;
      case 2:
        image = EyeClosed;
        break;
      default:
        image = EyeOpen;
        break;
    }
    return image;
  };

  const [windowDimensions, setWindowDimensions] = useState(
    getWindowDimensions(),
  );

  useEffect(() => {
    const interval = setInterval(() => {
      set_eye_mode((eye_mode+1)%10);
    },100)
    return () => clearInterval(interval);
  },[eye_mode]);

  useEffect(() => {
    const onResize = () => {
      setWindowDimensions(getWindowDimensions());
    };
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);


    return(
        <img src={getImage(eye_mode)} width={windowDimensions.width / 2} />
    );
}

export default DrawEye;
